 CustomizedExpandableListAdapter.java

 public class CustomizedExpandableListAdapter extends BaseExpandableListAdapter {
 private Context context;
 private List<String> expandableTitleList;
 private HashMap<String, List<String>> expandableDetailList;
 public CustomizedExpandableListAdapter(Context context, List<String> expandableTitleList, HashMap<String, List<String>> expandableDetailList) {
 this.context = context;
 this.expandableTitleList = expandableTitleList;
 this.expandableDetailList = expandableDetailList;}
 @Override
 public int getGroupCount() {
 return this.expandableTitleList.size();}
 @Override
 public int getChildrenCount(int groupPosition) {
 return this.expandableDetailList.get(this.expandableTitleList.get(groupPosition)).size();}
 @Override
 public Object getGroup(int groupPosition) {
 return this.expandableTitleList.get(groupPosition);}
 @Override
 public Object getChild(int groupPosition, int childPosition) {
 return this.expandableDetailList.get(this.expandableTitleList.get(groupPosition)).get(childPosition);}
 @Override
 public long getGroupId(int groupPosition) {return groupPosition;}
 @Override
 public long getChildId(int groupPosition, int childPosition) {return childPosition;}
 @Override
 public boolean hasStableIds() {return false;}
 @Override
 public View getGroupView(int groupPosition, boolean isExpanded, View convertView, ViewGroup parent) {
 String listTitle = (String) getGroup(groupPosition);
 if (convertView == null) {
 LayoutInflater layoutInflater = (LayoutInflater) this.context.
 getSystemService(Context.LAYOUT_INFLATER_SERVICE);
 convertView = layoutInflater.inflate(R.layout.list_group, null);}
 TextView listTitleTextView = (TextView) convertView.findViewById(R.id.listtitle);
 listTitleTextView.setTypeface(null, Typeface.BOLD);
 listTitleTextView.setText(listTitle);
 return convertView;}
 @Override
 public View getChildView(int groupPosition, int childPosition, boolean isLastChild, View convertView, ViewGroup parent) {
 final String expandedListText = (String) getChild(groupPosition, childPosition);
 if (convertView == null) {
 LayoutInflater layoutInflater = (LayoutInflater) this.context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);
 convertView = layoutInflater.inflate(R.layout.list_items, null);}
 TextView expandedListTextView = (TextView) convertView.findViewById(R.id.expandedListItem);
 expandedListTextView.setText(expandedListText);
 return convertView;}
 @Override
 public boolean isChildSelectable(int groupPosition, int childPosition) {return true;}
 } 

