 MainActivity.java

public class PointChart extends AppCompatActivity {
 @SuppressLint('MissingInflatedId')
 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 setContentView(R.layout.activity_point_chart);
 GraphView graphView = findViewById(R.id.idGraphView);
 PointsGraphSeries<DataPoint> series = new PointsGraphSeries<>(getDataPoint());
 graphView.addSeries(series);
 graphView.getViewport().setScrollable(true);
 graphView.getViewport().setScalable(true);
 graphView.getViewport().setScalableY(true);
 graphView.getViewport().setScrollableY(true);
 series.setShape(PointsGraphSeries.Shape.POINT);
 series.setSize(12);
 series.setColor(Color.GREEN);}
 private DataPoint[] getDataPoint() {
 DataPoint[] dataPoints = new DataPoint[]{
 new DataPoint(0, 1),
 new DataPoint(1, 2),
 new DataPoint(2, 3),
 new DataPoint(3, 5),
 new DataPoint(4, 1),
 new DataPoint(4, 3),
 new DataPoint(5, 3),
 new DataPoint(6, 2)};
 return dataPoints;}
